mapper.select:
select p.no, p.code, p.name, p.manufacturer, p.price, p.description, p.category_no as categoryNo, p.sales_status_no as salesStatusNo, e.no, group_concat(e.title separator '/') as eventTitle, group_concat(e.rate separator '/') as eventRate, ss.status, c.name as categoryName from product p, product_event pe, sales_status ss, category c, event e where p.sales_status_no = ss.no and p.no = pe.product_no and pe.event_no = e.no and p.category_no = c.no and p.live = 'y'
and p.sales_status_no = ${opt.status}
and p.category_no = ${opt.category}
and pe.event_no = ${opt.event}
and p.${opt.option} like '%${opt.keyword}%'
group by p.no limit #{startCount},#{countList}

		
			select count(*) 
			from(
				select * 
				from product p, product_event pe 
				where live = 'y' 
	            and p.no = pe.product_no 
	       
		
		
			
				and p.sales_status_no = ${status}   
						
		
		
			
				and p.category_no = ${category}    
						
		
		
			
				and pe.event_no = ${event}   
						
		
		
			
				and p.${option} like '%${keyword}%'  
						
		
		
		     group by no
            ) t;
